import React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { StackRouter } from '../constants/StackRouter';
import {Header, HighlightButton} from '../components';

const HomeScreen = ({navigation}:StackRouter) => {
    return (
        <View style={styles.container}>
            <Header content="CityPop"/>
            <View style={styles.body}>
                <View style={styles.btnContainer}>
                    <HighlightButton content='Search By City' route='Search By City' navigation={navigation}/>
                </View>
                <View style={styles.btnContainer}>
                    <HighlightButton content='Search By Country' route='Search By Country' navigation={navigation}/>
                </View>
            </View>       
        </View>
    );
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        flexDirection: "column",
        paddingVertical: 30,
        paddingHorizontal: 30,
        backgroundColor: '#E7E7E7',    
    },
    body: {
        flex: 2,
        flexDirection: "column",
        justifyContent: "flex-start",
        padding: 40,
    },
    btnContainer: {
        padding: 5,
    }
});
export default HomeScreen;
