import React, { useEffect, useState } from 'react';
import { 
  ActivityIndicator, 
  FlatList, 
  ListRenderItemInfo, 
  Text, 
  View } from 'react-native';
import { geonames } from '../constants/Interfaces';
import { CountriesProps } from '../constants/RootStack';
import { Header } from '../components'

export default class CountriesScreen extends React.Component {
  state = {
    geonames: [],
    isLoading: false,
  }

  async getGeo() {
    const getGeo = async () => {
      try {
       const response =  await fetch(
         'http://api.geonames.org/searchJSON?username=weknowit&maxRows=10&q='
         +this.props.inputText);
       const json = await response.json();
       this.setState({geonames: json.geonames});
     } catch (error) {
       console.error(error);
     } finally {
       setLoading(false);
     }
   }
  }
}

