import React, { useEffect, useState } from 'react';
import { 
  ActivityIndicator, 
  FlatList, 
  ListRenderItemInfo, 
  Text, 
  View } from 'react-native';
import { geonames } from '../constants/Interfaces';
import { CountriesProps } from '../constants/RootStack';
import { Header } from '../components'

type State = {
  geonames: Array<geonames>,
  isLoading: boolean,
  inputText: string,
}

export default class CountriesScreen extends React.Component<CountriesProps, State> {
  constructor(props: string) {
    super(props);
    this.state = {
      geonames: [],
      isLoading: true,
      inputText: props,
    }
  
  }

  async getGeo() {
    try {
      const response =  await fetch(
        'http://api.geonames.org/searchJSON?username=weknowit&maxRows=10&q='
        +this.state.inputText.route.params.inputText);
      const json = await response.json();
      this.setState({geonames: json.geonames});
    } catch (error) {
      console.error(error);
    } finally {
      this.setState({isLoading: false});
    }
  }

  async componentDidMount() {
    await this.getGeo();
    console.log(this.state.geonames[0].name)
  }

  render () {
    return(
      <View style={{ flex: 1, padding: 24 }}>
        <Header content={this.props.route.params.inputText}/>
        {/* {console.log(getCountryCode(geo[0].value))} */}

        {this.state.isLoading ? <ActivityIndicator/> : (
          <FlatList
            data={this.state.geonames}
            keyExtractor={( item: geonames) => item.name}
            renderItem={({ item }: ListRenderItemInfo<geonames>) => (
              <Text>{item.name}, {item.population}</Text>
            )}
          />
        )}
      </View>
    );
  }
}

